import { useEffect, useState } from 'react';
import api from '../api';

function UsersPage() {
    const [users, setUsers] = useState([]);
    const [form, setForm] = useState({
        name: '',
        email: '',
        password: '',
        role: 'MORADOR',
        endereco_completo: '',
        data_nascimento: '',
    });

    const loadUsers = () => {
        api.get('/api/users')
            .then(res => setUsers(res.data))
            .catch(err => {
                console.error('Erro ao carregar usuários', err);
                alert('Erro ao carregar usuários. Veja o console.');
            });
    };

    useEffect(() => {
        loadUsers();
    }, []);

    const handleChange = (e) => {
        setForm(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        api.post('/api/users', form)
            .then(() => {
                alert('Usuário cadastrado com sucesso!');
                setForm({
                    name: '',
                    email: '',
                    password: '',                    
                    address: '',
                    birth_date: '',
                    role: 'MORADOR',
                });
                loadUsers();
            })
            .catch(err => {
                console.error('Erro ao cadastrar usuário', err);
                const msg = err.response?.data?.message || 'Erro ao cadastrar usuário';
                alert(msg);
            });
    };

    const handleDelete = (id) => {
        if (!window.confirm('Tem certeza que deseja excluir este usuário?')) return;

        api.delete(`/api/users/${id}`)
            .then(() => {
                loadUsers();
            })
            .catch(err => {
                console.error('Erro ao excluir usuário', err);
                alert('Erro ao excluir usuário.');
            });
    };

    return (
        <div>
            <h2 className="mb-3">Usuários</h2>

            <div className="card mb-4">
                <div className="card-body">
                    <h5 className="card-title">Cadastrar novo usuário</h5>
                    <form className="row g-3" onSubmit={handleSubmit}>
                        
                        <div className="col-12 col-md-4">
                            <label className="form-label">Nome</label>
                            <input
                                className="form-control"
                                name="name"
                                value={form.name}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="col-12 col-md-4">
                            <label className="form-label">Email</label>
                            <input
                                type="email"
                                className="form-control"
                                name="email"
                                value={form.email}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="col-12 col-md-4">
                            <label className="form-label">Senha</label>
                            <input
                                type="password"
                                className="form-control"
                                name="password"
                                value={form.password}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="col-12 col-md-6">
                            <label className="form-label">Endereço Completo</label>
                            <input
                                className="form-control"
                                name="endereco_completo"
                                value={form.endereco_completo}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="col-12 col-md-3">
                            <label className="form-label">Data de Nascimento</label>
                            <input
                                type="date"
                                className="form-control"
                                name="data_nascimento"
                                value={form.data_nascimento}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="col-12 col-md-3">
                            <label className="form-label">Função</label>
                            <select
                                className="form-select"
                                name="role"
                                value={form.role}
                                onChange={handleChange}
                            >
                                <option value="MORADOR">Morador</option>
                                <option value="ADMIN">Admin</option>
                            </select>
                        </div>

                        <div className="col-12">
                            <button className="btn btn-success" type="submit">
                                Salvar usuário
                            </button>
                        </div>
                    </form>
                </div>
            </div>

            <h5 className="mb-3">Usuários cadastrados</h5>
            <div className="table-responsive">
                <table className="table table-striped align-middle">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Nome</th>
                            <th>Email</th>
                            <th>Função</th>
                            <th>Cadastrado em</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(u => (
                            <tr key={u.id}>
                                <td>{u.id}</td>
                                <td>{u.name}</td>
                                <td>{u.email}</td>
                                <td>{u.role}</td>
                                <td>{new Date(u.created_at).toLocaleString('pt-BR')}</td>
                                <td>
                                    <button
                                        className="btn btn-sm btn-outline-danger"
                                        onClick={() => handleDelete(u.id)}
                                    >
                                        Excluir
                                    </button>
                                </td>
                            </tr>
                        ))}
                        {users.length === 0 && (
                            <tr>
                                <td colSpan="6">Nenhum usuário cadastrado ainda.</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default UsersPage;
