import { useState, useEffect } from 'react';
import api from "../api";

function DeliveriesPage() {
    const [users, setUsers] = useState([]);
    const [points, setPoints] = useState([]);
    const [wasteTypes, setWasteTypes] = useState([]);
    const [deliveries, setDeliveries] = useState([]);

    const [form, setForm] = useState({
        user_id: "",
        collection_point_id: "",
        waste_type_id: "",
        quantity_kg: ""
    });

    useEffect(() => {
        let ignore = false;

        const loadData = async () => {
            try {
                const [u, p, w, d] = await Promise.all([
                    api.get("/api/users"),
                    api.get("/api/points"),
                    api.get("/api/waste-types"),
                    api.get("/api/deliveries"),
                ]);

                if (!ignore) {
                    setUsers(u.data);
                    setPoints(p.data);
                    setWasteTypes(w.data);
                    setDeliveries(d.data);
                }
            } catch (error) {
                console.error("Erro ao carregar dados iniciais", error);
            }
        };

        loadData();
        return () => { ignore = true };
    }, []);

    const handleChange = (e) => {
        setForm(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const payload = {
            user_id: Number(form.user_id),
            collection_point_id: Number(form.collection_point_id),
            items: [
                {
                    waste_type_id: Number(form.waste_type_id),
                    quantity_kg: Number(form.quantity_kg)
                }
            ]
        };

        api.post("/api/deliveries", payload)
            .then(() => {
                alert("Entrega registrada com sucesso!");
                setForm({
                    user_id: "",
                    collection_point_id: "",
                    waste_type_id: "",
                    quantity_kg: ""
                });

                api.get("/api/deliveries").then(res => setDeliveries(res.data));
            })
            .catch(err => {
                console.error("Erro ao registrar entrega", err);
                alert("Erro ao registrar entrega. Veja o console.");
            });
    };

    return (
        <div>
            <h2 className="mb-3">Entregas de Resíduos</h2>

            <div className="card mb-4">
                <div className="card-body">
                    <h5 className="card-title">Registrar nova entrega</h5>
                    <form className="row g-3" onSubmit={handleSubmit}>

                        <div className="col-12 col-md-4">
                            <label className="form-label">Usuário</label>
                            <select
                                className="form-select"
                                name="user_id"
                                value={form.user_id}
                                onChange={handleChange}
                                required
                            >
                                <option value="">Selecione</option>
                                {users.map(u => (
                                    <option key={u.id} value={u.id}>{u.name}</option>
                                ))}
                            </select>
                        </div>

                        <div className="col-12 col-md-4">
                            <label className="form-label">Ponto de Coleta</label>
                            <select
                                className="form-select"
                                name="collection_point_id"
                                value={form.collection_point_id}
                                onChange={handleChange}
                                required
                            >
                                <option value="">Selecione</option>
                                {points.map(p => (
                                    <option key={p.id} value={p.id}>{p.name}</option>
                                ))}
                            </select>
                        </div>

                        <div className="col-12 col-md-4">
                            <label className="form-label">Tipo de Resíduo</label>
                            <select
                                className="form-select"
                                name="waste_type_id"
                                value={form.waste_type_id}
                                onChange={handleChange}
                                required
                            >
                                <option value="">Selecione</option>
                                {wasteTypes.map(w => (
                                    <option key={w.id} value={w.id}>{w.name}</option>
                                ))}
                            </select>
                        </div>

                        <div className="col-12 col-md-3">
                            <label className="form-label">Quantidade (kg)</label>
                            <input
                                type="number"
                                min="0.1"
                                step="0.1"
                                className="form-control"
                                name="quantity_kg"
                                value={form.quantity_kg}
                                onChange={handleChange}
                                required
                            />
                        </div>

                        <div className="col-12">
                            <button className="btn btn-success" type="submit">
                                Registrar entrega
                            </button>
                        </div>

                    </form>
                </div>
            </div>

            <h5 className="mb-3">Entregas registradas</h5>
            <div className="table-responsive">
                <table className="table table-striped align-middle">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Usuário</th>
                            <th>Ponto de Coleta</th>
                            <th>Data</th>
                            <th>Total (kg)</th>
                        </tr>
                    </thead>
                    <tbody>
                        {deliveries.map(d => (
                            <tr key={d.id}>
                                <td>{d.id}</td>
                                <td>{d.user_name}</td>
                                <td>{d.point_name}</td>
                                <td>{new Date(d.created_at).toLocaleString("pt-BR")}</td>
                                <td>{Number(d.total_kg || 0).toFixed(2)}</td>
                            </tr>
                        ))}
                        {deliveries.length === 0 && (
                            <tr>
                                <td colSpan="5">Nenhuma entrega registrada ainda.</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default DeliveriesPage;
